import { Morph, Icon, Label } from 'lively.morphic';
import { Button } from 'lively.components/index.js';
import { signal } from 'lively.bindings/index.js';
import { pt } from 'lively.graphics/index.js';
import { fun, arr } from "lively.lang/index.js";

export default class IconButton extends Button {
  static get properties () {
    return {
      // if true, the button is always as small as possible
      autoResize: {
        after: ['icon'],
        defaultValue: true,
        set (autoResize) {
          this.setProperty('autoResize', autoResize);
          this.requestFit();
        }
      },

      padding: {
        set (padding) {
          this.setProperty('padding', padding);
          this.requestFit();
        }
      },

      fontSize: {
        readOnly: true,
        serialize: false,
        // overwrite getter/setter of super class
        set () { },
        get () { }
      },

      fontColor: {
        readOnly: true,
        serialize: false,
        // overwrite getter/setter of super class
        set () { },
        get () { }
      },

      icon: {
        set (iconNameOrMorph) {
          const existing = this.icon;
          if (existing) existing.remove();
          this.setProperty('icon', undefined);

          if (!iconNameOrMorph) {
            this.requestFit();
          } else {
            const iconMorph =
              (iconNameOrMorph.isMorph) ? iconNameOrMorph : new Label();
            this.setProperty('icon', iconMorph);

            iconMorph.name = 'icon';
            this.addMorphAt(iconMorph, 0);
            if (!iconNameOrMorph.isMorph) Icon.setIcon(this.icon, iconNameOrMorph);

            // wait until master request styling was applied in addMorphAt
            this.whenRendered().then(() => this.requestFit());
          }
        }
      },

      labelMorph: {
        after: ['submorphs'],
        // overwrite super class getter/setter
        set (labelMorph) { },
        get () {
          return this._labelMorph;
        }
      },

      label: {
        after: ['submorphs'],
        isStyleProp: false,
        set (string) {
          this.setProperty('label', string);
          this.labelMorph.textString = string;
          this.requestFit();
          this.refreshTooltip();
        },
        get () {
          return this.getProperty('label');
        }
      },

      // if true, show only the icon
      collapsed: {
        after: ['submorphs', 'autoResize', 'tooltip', 'layout'],
        defaultValue: false,
        isStyleProp: false,
        set (collapsed) {
          this.setProperty('collapsed', collapsed);
          if (collapsed) {
            this._uncollapsedWidth = this.width;
            this.labelMorph.remove();
            this.refreshTooltip();
            this.requestFit();
          } else {
            this.addMorphAt(this.labelMorph, 1);
            // wait until the master request styling was applied in addMorphAt
            this.whenRendered().then(() => this.requestFit());
            this.width = this._uncollapsedWidth;
            this.refreshTooltip();
            this.requestMasterStyling();
          }
        }
      },

      mastersButtonActivated: {
        isStyleProp: false
      },
      masterButtonDeactivated: {
        isStyleProp: false,
        initialize () {
          // this.masterButtonDeactivated = 'styleguide://QinoqWidgets/icon button/disabled';
        }
      },

      deactivated: {
        defaultValue: false,
        after: ['label'],
        isStyleProp: false,
        set (deactivated) {
          this.setProperty('deactivated', deactivated);
          if (deactivated) {
            this.mastersButtonActivated = this.master;
            this.master = this.masterButtonDeactivated;
            return;
          }
          this.master = this.mastersButtonActivated;
        }
      },

      _uncollapsedWidth: { },
      _uncollapsedAutoResize: { },

      alwaysShowDescription: {
        defaultValue: false,
        isStyleProp: false,
        set (showDescription) {
          this.setProperty('alwaysShowDescription', showDescription);
          this.refreshTooltip();
        }
      },

      description: {
        set (description) {
          this.setProperty('description', description);
          this.refreshTooltip();
        }
      },

      target: { },
      action: {
        // overwrite non-serialization of super class
        serialize: true
      },
      args: { },

      keybinding: {
        set (keybinding) {
          this.setProperty('keybinding', keybinding);
          this.refreshTooltip();
        }
      },

      command: {
        set (commandName) {
          this.setProperty('commandName', commandName);
          const command = $world.lookupCommand(commandName);
          this.action = 'execCommand';
          this.args = [commandName];
          this.keybinding = $world.keysForCommand(commandName);
        }
      },

      extent: {
        after: ['padding', 'icon']
      }
    };
  }

  get _labelMorph () {
    return this.getSubmorphNamed('label') || new Label({
      name: 'label',
      textString: this.label || 'label'
    });
  }

  trigger () {
    try {
      signal(this, 'fire');
      if (this.target) {
        this.target[this.action](
          ...(typeof this.args === 'object' ? this.args : [this.args])
        );
      }
    } catch (err) {
      const w = this.world();
      if (w) w.logError(err);
      else console.error(err);
    }
  }

  onChange (change) {
    // make sure extent has no side effects when button is collapsed to make layout work
    const { prop } = change;
    if (prop === 'extent' && this.autoResize && !this._blockExtentUpdate) {
      this.requestFit();
      return;
    }
    return super.onChange(change);
  }

  relayout () {

  }

  refreshTooltip () {
    const keyString = (this.keybinding) ? this.keybinding : '';

    const tooltip = this.collapsed && !this.alwaysShowDescription
      ? this.label
      : this.description;

    if (!tooltip || tooltip === '' || tooltip === ' ') {
      this.tooltip = keyString;
      return;
    }

    this.tooltip = (keyString === '') ? tooltip : `${tooltip} (${keyString})`;
  }

  requestFit () {
    this.opacity = 0;
    fun.debounceNamed('requestFit' + this.id, 50, () => {
      // wait for fonts to be loaded to calculate proper sizes
      const promises = [];
      const label = this.collapsed ? undefined : this.labelMorph;
      const icon = this.get('icon');
      // if (icon) promises.push(icon.whenFontLoaded());
      // if (label) promises.push(label.whenFontLoaded());
      // wait for layout to be set to read spacing
      if (!this.layout) promises.push(this.whenRendered());
      Promise.all(promises).then(() => this.fit()).then(() => this.opacity = 1);
    })();
  }

  async fit () {
    const padding = this.padding;
    const label = this.collapsed ? undefined : this.labelMorph;
    const icon = this.getSubmorphNamed('icon');

    const spacing = this.collapsed ? 0 : this.layout.spacing;

    if (label) label.fit();
    if (icon) icon.fit();

    if (!this.autoResize && !this.collapsed) return this;

    this._blockExtentUpdate = true;

    const labelExtent = label ? label.extent : pt(0, 0);
    const iconExtent = icon ? icon.extent : pt(0, 0);

    const contentHeight = Math.max(labelExtent.y, iconExtent.y);
    this.extent = padding
      .bottomLeft()
      .addPt(padding.topRight())
      .addXY(iconExtent.x + labelExtent.x + spacing, contentHeight);

    delete this._blockExtentUpdate;
    return this;
  }
}
