import { Button } from 'lively.components';
import { Icon } from 'lively.morphic/text/icons.js';
import { Label } from 'lively.morphic/index.js';

export default class IconButton extends Button {
  static get properties () {
    return {
      autoResize: {
        after: ['icon'],
        defaultValue: true,
        set (autoResize) {
          this.setProperty('autoResize', autoResize);
          if (!this.collapsed && autoResize) this.fit();
        }
      },

      fontSize: {
        type: 'Number',
        derived: false,
        after: ['submorphs', 'label', 'icon', 'autoResize'],
        set (fontSize) {
          if (!fontSize) return;
          this.setProperty('fontSize', fontSize);
          this._labelMorph.fontSize = fontSize;
          if (this.icon) this.icon.fontSize = fontSize;
          if (this.autoResize) this.fit();
        },
        // overwrite getter of super class
        get () {
          return this.getProperty('fontSize');
        }
      },

      fontColor: {
        after: ['submorphs', 'label'],
        set (fontColor) {
          this.setProperty('fontColor', fontColor);
          this._labelMorph.fontColor = fontColor;
          if (this.icon) this.icon.fontColor = fontColor;
        },
        get () {
          return this.getProperty('fontColor');
        }
      },

      icon: {
        after: [],
        set (iconNameOrMorph) {
          const existing = this.icon;
          if (existing) existing.remove();
          this.setProperty('icon', undefined);

          if (!iconNameOrMorph) return;

          const iconMorph = (iconNameOrMorph.isMorph) ? iconNameOrMorph : new Label();
          iconMorph.fontSize = this.fontSize;
          iconMorph.fontColor = this.fontColor;
          iconMorph.name = 'icon';
          iconMorph.reactsToPointer = false;
          this.addMorphAt(iconMorph, 0);

          this.setProperty('icon', iconMorph);

          if (!iconNameOrMorph.isMorph) Icon.setIcon(this.icon, iconNameOrMorph);
        }
      },

      labelMorph: {
        after: ['submorphs'],
        // overwrite super class
        set (labelMorph) { },
        get () {
          return this._labelMorph;
        }
      },

      label: {
        after: ['submorphs'],
        set (string) {
          this.setProperty('label', string);
          this._labelMorph.textString = string;
          if (this.autoResize) this.fit();
        }
      },

      // only show Icon if true
      collapsed: {
        after: ['submorphs', 'autoResize'],
        defaultValue: false,
        set (collapsed) {
          this.setProperty('collapsed', collapsed);
          if (collapsed) {
            this._uncollapsedWidth = this.width;
            this._labelMorph.remove();
            this._uncollapsedAutoResize = this.autoResize;
            this.layout.autoResize = true;
            this.refreshTooltip();
          } else {
            this.addMorphAt(this._labelMorph, 1);
            this.layout.autoResize = false;
            this.autoResize = this._uncollapsedAutoResize;
            this.width = this._uncollapsedWidth;
            this.refreshTooltip();
          }
        }
      },

      deactivated: {
        defaultValue: false,
        after: ['label'],
        set (deactived) {
          this.setProperty('deactivated', deactived);
          if (deactived) {
            this.master = 'styleguide://$world/icon button/disabled';
            return;
          }
          this.master = {
            auto: 'styleguide://$world/icon button/default',
            hover: 'styleguide://$world/icon button/hover',
            click: 'styleguide://$world/icon button/active'
          };
        }
      },

      _uncollapsedWidth: { },
      _uncollapsedAutoResize: { },

      longTooltip: {
        set (tooltip) {
          this.setProperty('longTooltip', tooltip);
          this.refreshTooltip();
        }
      },

      keybinding: {
        set (keybinding) {
          this.setProperty('keybinding', keybinding);
          this.refreshTooltip();
        }
      },

      command: {
        set (commandName) {
          this.setProperty('commandName', commandName);
          const command = $world.lookupCommand(commandName);
          this.action = command ? () => $world.execCommand(commandName) : undefined;
          this.keybinding = $world.keysForCommand(commandName);
        }
      },

      extent: {
        after: ['icon']
      }
    };
  }

  get _labelMorph () {
    return this.getSubmorphNamed('label') || new Label({
      fontSize: this.fontSize,
      reactsToPointer: false,
      name: 'label',
      textString: this.label || 'label'
    });
  }

  onChange (change) {
    // make sure extent has no side effects when button is collapsed to make layout work
    const { prop } = change;
    if (this.collapsed && prop === 'extent') return;
    if (prop === 'extent' && this.autoResize && !this._blockExtentUpdate) { this.fit(); }
    return super.onChange(change);
  }

  refreshTooltip () {
    const keyString = (this.keybinding) ? this.keybinding : '';

    const tooltip = this.collapsed ? this.label : this.longTooltip;

    if (!tooltip || tooltip === '' || tooltip === ' ') {
      this.tooltip = keyString;
      return;
    }

    this.tooltip = (keyString === '') ? tooltip : `${tooltip} (${keyString})`;
  }

  fit () {
    const padding = this.padding;
    const label = this.collapsed ? undefined : this._labelMorph;
    const icon = this.icon;
    const spacing = this.layout ? this.layout.spacing : 0;

    if (!label || !icon) return;

    label.fit();
    this._blockExtentUpdate = true;
    const contentHeight = Math.max(label.height, icon.height);
    this.extent = padding
      .bottomLeft()
      .addPt(padding.topRight())
      .addXY(icon.width + label.width + spacing, contentHeight);
    this.relayout();
    delete this._blockExtentUpdate;
    return this;
  }
}
