KeyframeButton.index.js:
import { Morph } from "lively.morphic";

export default class KeyframeButton extends Morph {
  static get properties () {
    return {
      selected: {
        defaultValue: false,
        set (selected) {
          this.setProperty('selected', selected);
          this.setAppearance();
        }
      }
    };
  }

  setAppearance () {
    if (this.selected) {
      this.master = $world.getSubmorphNamed('keyframe button/selected');
    } else {
      this.master = {
        auto: $world.getSubmorphNamed('keyframe button/default'),
        hover: $world.getSubmorphNamed('keyframe button/hover'),
        click: $world.getSubmorphNamed('keyframe button/click')
      };
    }
  }

  onMouseUp () {
    this.selected = !this.selected;
  }
}
