import { Morph } from "lively.morphic";
import { pt } from "lively.graphics/index.js";
import { resource } from "lively.resources/src/helpers.js";

export default class Tabs extends Morph {
  setBounds (bounds) {
    super.setBounds(bounds);
    const contentContainer = this.getSubmorphNamed('tab content container');
    const flapsContainer = this.getSubmorphNamed('tab flap container');

    contentContainer.extent = pt(bounds.width, bounds.height - flapsContainer.height);
    contentContainer.position = pt(0, flapsContainer.top + flapsContainer.height);
  }

  async addTab (caption, content = null) {
    const newTab = await resource('part://$world/tab flap').read();
    newTab.caption = caption;
    newTab.content = content;

    const tabFlapContainer = this.getSubmorphNamed('tab flap container');
    const newTabButton = this.getSubmorphNamed('button new tab');
    tabFlapContainer.addMorph(newTab, newTabButton);

    newTab.selected = true;
    return newTab;
  }

  deselectAllTabs () {
    this.getSubmorphNamed('tab flap container').tabs.forEach(tab => {
      tab.selected = false;
    });
  }

  selectNearestTab (otherTab) {
    const tabs = this.getSubmorphNamed('tab flap container').tabs;
    if (tabs.length === 0) return;
    if (tabs.length === 1) {
      tabs[0].selected = true;
      return;
    }
    let tabIndex = tabs.indexOf(otherTab);
    const tab = tabIndex < tabs.length - 1 ? tabs[++tabIndex] : tabs[--tabIndex];
    tab.selected = true;
    return tab;
  }

  showContent (content) {
    const container = this.getSubmorphNamed('tab content container');
    container.withAllSubmorphsDo(submorph => {
      if (submorph !== container) submorph.remove();
    });

    if (content) {
      container.addMorph(content);
      content.position = pt(0, 0);
    }
  }
}

